	$(document).ready(function(){
		date_time();
		$(".sidebar-box").css('height', h+'px');
		$(".search-query").on("focus", function(){
			$(".search-icon").removeClass("text-light");
			$(".search-icon").addClass("text-dark");
		});
		$(".search-query").on("blur", function(){
			$(".search-icon").removeClass("text-dark");
			$(".search-icon").addClass("text-light");
		});
		$(".setting-btn").click(function(){
			$(".sidebar-box").removeClass("d-none");
			$(".slide-close").click(function(){
				$(".sidebar-box").addClass("d-none");
			});
		});
		share_btn();
	});
		
		function share_btn(){
			$(".share-btn").click(function(){
			$(".share-icons").removeClass("d-none");
			$(this).removeClass("fa fa-arrow-righ");
			$(this).addClass("fa fa-arrow-down");
			$(this).click(function(){
				$(".share-icons").addClass("d-none");
				$(this).removeClass("fa fa-arrow-down");
				$(this).addClass("fa fa-arrow-right");
				share_btn(); 
			});
		});
		}
/**
 * This code only for time and date
 * Don not edit this code
*/	

function date_time()
	{
		 var d =  new Date();
		 var date = d.getDate();
		 var day = d.getDay();
		 var month = d.getMonth();
		 var hours = d.getHours();
		 var minuts = d.getMinutes();
		 if(minuts == '0' || minuts == '0' || minuts == '1' || minuts == '2' || minuts == '3' || minuts == '4' || minuts == '5' || minuts == '6' || minuts == '7' || minuts == '8' || minuts == '9')
		 {
		 	minuts = '0'+minuts;
		 }
		 if(hours == '0' || hours == '0' || hours == '1' || hours == '2' || hours == '3' || hours == '4' || hours == '5' || hours == '6' || hours == '7' || hours == '8' || hours == '9')
		 {
		 	hours = '0'+hours;
		 }


		 var weakday = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
		 var Months = ["January","February","March","April","May","June","July","August","September","October","November","December"];
		 day = weakday[day];
		 month = Months[month];
		 time = hours+":"+minuts;
		 date = day+", "+month+" "+date;
		 	$(".time").html(time);
		    $(".date").html(date);
		
		 
		 setInterval(function(){
		 	date_time();
		 },6000);
	}

